mapper.select:
select c.*, r.roomNo as roomNo, t.buildingNo as buildingNo,t.buildingName as buildingName, u.userName as userName,u.profession as profession,u.classNO as classNO,u.phone as phone,u.type as uType from course_arrange c left join building_room r on c.roomId=r.id left join building_type t on t.id=r.buildingId left join user u on u.id=c.userId where c.status='0'
and c.userId=#{course.userId}
and u.userName=#{course.userName}
and c.userId=#{course.userId}
and c.date >=#{course.startDate}
and c.date <=#{course.endDate}
and c.type = 8 union select c.*, r.roomNo as roomNo, t.buildingNo as buildingNo,t.buildingName as buildingName, u.userName as userName,u.profession as profession,u.classNO as classNO,u.phone as phone,u.type as uType from course_arrange c left join building_room r on c.roomId=r.id left join building_type t on t.id=r.buildingId left join user u on u.id=c.userId where c.type in (0,1,2,3,4,5,6,7) and c.status='0'
/*order by c.date,c.startTime desc*/
